// Collect all unique LinkedIn URLs from all pages
const allItems = $input.all();
const uniqueUrls = new Map();

// Deduplicate URLs
allItems.forEach(item => {
    if (item.json.linkedin_url) {
        uniqueUrls.set(item.json.linkedin_url, item.json.linkedin_url);
    }
});

const urlArray = Array.from(uniqueUrls.values());
console.log(`Collected ${urlArray.length} unique LinkedIn URLs total`);

// Prepare for APIFY
return [{
    json: {
        profileUrls: urlArray,
        totalUrls: urlArray.length
    }
}];

// Process LinkedIn data from the new structure
const items = [];
// Get all items from the previous node
const profiles = $items().map(item => item.json);
console.log(`Processing ${profiles.length} profiles from LinkedIn API`);

profiles.forEach(profileData => {
    if (!profileData || !profileData.element) return;

    // Extract the actual profile data from the element
    const profile = profileData.element;

    // Extract skills from the skills array
    let skillsList = '';
    if (profile.skills && Array.isArray(profile.skills)) {
        skillsList = profile.skills
            .map(skill => skill.name || '')
            .filter(name => name.length > 0)
            .slice(0, 10) // Limit to first 10 skills
            .join(', ');
    }

    // Extract education information
    let educationList = '';
    if (profile.education && Array.isArray(profile.education)) {
        educationList = profile.education
            .map(edu => {
                const parts = [];
                if (edu.degree) parts.push(edu.degree);
                if (edu.fieldOfStudy) parts.push(edu.fieldOfStudy);
                if (edu.schoolName) parts.push(`at ${edu.schoolName}`);
                if (edu.period) parts.push(`(${edu.period})`);
                return parts.join(' ');
            })
            .filter(edu => edu.length > 0)
            .join('; ');
    }

    // Extract certifications
    let certifications = '';
    if (profile.certifications && Array.isArray(profile.certifications)) {
        certifications = profile.certifications
            .map(cert => cert.title || '')
            .filter(title => title.length > 0)
            .join(', ');
    }

    // Extract current position
    let currentJobTitle = '';
    let currentCompany = '';
    if (profile.currentPosition && Array.isArray(profile.currentPosition) && profile.currentPosition.length > 0) {
        currentCompany = profile.currentPosition[0].companyName || '';
    }

    // Get job title from experience if available
    if (profile.experience && Array.isArray(profile.experience) && profile.experience.length > 0) {
        const currentJob = profile.experience[0]; // Most recent experience
        if (currentJob.endDate && currentJob.endDate.text === 'Present') {
            currentJobTitle = currentJob.position || '';
            if (!currentCompany) {
                currentCompany = currentJob.companyName || '';
            }
        }
    }

    // Parse location
    let location = '';
    if (profile.location) {
        if (profile.location.parsed && profile.location.parsed.text) {
            location = profile.location.parsed.text;
        } else if (profile.location.linkedinText) {
            location = profile.location.linkedinText;
        }
    }

    const processedProfile = {
        linkedin_url: profile.linkedinUrl || '',
        full_name: `${profile.firstName || ''} ${profile.lastName || ''}`.trim(),
        headline: profile.headline || '',
        email: 'Not provided', // Email is not provided in this structure
        current_job_title: currentJobTitle || 'Not specified',
        current_company: currentCompany || 'Not specified',
        company_industry: 'Not specified', // Not available in this structure
        experience_years: 0, // Would need to calculate from experience dates
        location: location || '',
        skills: skillsList || 'Not listed',
        education: educationList || 'Not provided',
        about_summary: (profile.about || '').substring(0, 500) || 'No summary available',
        connections: profile.connectionsCount || 0,
        certifications: certifications || 'None listed',
        follower_count: profile.followerCount || 0,
        is_premium: profile.premium || false,
        is_influencer: profile.influencer || false,
        is_verified: profile.verified || false
    };

    items.push({ json: processedProfile });
});

console.log(`Successfully processed ${items.length} LinkedIn profiles`);
return items;

const items = [];
const inputData = $items().map(item => item.json);

inputData.forEach(item => {
    if (!item || !item.output) return;

    try {
        let jsonStr = item.output;

        if (jsonStr.includes('```json')) {
            jsonStr = jsonStr.replace(/```json\n?/g, '').replace(/```\n?/g, '');
        }

        const parsedData = JSON.parse(jsonStr.trim());

        const processedData = {
            linkedin_url: parsedData.linkedin_url || '',
            full_name: parsedData.full_name || '',
            headline: parsedData.headline || '',
            email: parsedData.email || 'N/A',
            current_job_title: parsedData.current_job_title || '',
            current_company: parsedData.current_company || '',
            company_industry: parsedData.company_industry || '',
            experience_years: parsedData.experience_years || 0,
            location: parsedData.location || '',
            skills: parsedData.skills || '',
            education: parsedData.education || '',
            about_summary: parsedData.about_summary || '',
            overall_fit_score: parsedData.overall_fit_score || 0,
            experience_match: parsedData.experience_match || 0,
            skills_match: parsedData.skills_match || 0,
            education_match: parsedData.education_match || 0,
            location_fit: parsedData.location_fit || 0,
            ai_recommendation: parsedData.ai_recommendation || '',
            key_strengths: parsedData.key_strengths || '',
            potential_concerns: parsedData.potential_concerns || '',
            interview_questions: Array.isArray(parsedData.interview_questions) 
                ? parsedData.interview_questions.join(' | ') 
                : parsedData.interview_questions || ''
        };

        items.push({ json: processedData });

    } catch (error) {
        items.push({
            json: {
                linkedin_url: '',
                full_name: 'PARSING ERROR',
                headline: '',
                email: 'N/A',
                current_job_title: '',
                current_company: '',
                company_industry: '',
                experience_years: 0,
                location: '',
                skills: '',
                education: '',
                about_summary: 'Error parsing data',
                overall_fit_score: 0,
                experience_match: 0,
                skills_match: 0,
                education_match: 0,
                location_fit: 0,
                ai_recommendation: 'ERROR',
                key_strengths: '',
                potential_concerns: 'Data parsing failed',
                interview_questions: ''
            }
        });
    }
});

return items;
